const request = require('request');

const options = {
  method: 'POST',
  url: 'https://api.dojo.tech/payment-intents',
  headers: {
    'content-type': 'application/json',
    version: 'SOME_STRING_VALUE',
    Authorization: 'Basic REPLACE_BASIC_AUTH'
  },
  body: {
    amount: {value: 1000, currencyCode: 'GBP'},
    reference: 'Order 234',
    description: 'Demo payment intent'
  },
  json: true
};

request(options, function (error, response, body) {
  if (error) throw new Error(error);

  console.log(body);
});

var client = new RestClient("https://api.dojo.tech/payment-intents");
var request = new RestRequest(Method.POST);
request.AddHeader("content-type", "application/json");
request.AddHeader("version", "SOME_STRING_VALUE");
request.AddHeader("Authorization", "Basic REPLACE_BASIC_AUTH");
request.AddParameter("application/json", "{\"amount\":{\"value\":1000,\"currencyCode\":\"GBP\"},\"reference\":\"Order 234\",\"description\":\"Demo payment intent\"}", ParameterType.RequestBody);
IRestResponse response = client.Execute(request);

