curl_easy_setopt(hnd, CURLOPT_URL, "https://api.dojo.tech/payment-intents");

struct curl_slist *headers = NULL;
headers = curl_slist_append(headers, "content-type: application/json");
headers = curl_slist_append(headers, "version: SOME_STRING_VALUE");
headers = curl_slist_append(headers, "Authorization: Basic REPLACE_BASIC_AUTH");
curl_easy_setopt(hnd, CURLOPT_HTTPHEADER, headers);

curl_easy_setopt(hnd, CURLOPT_POSTFIELDS, "{\"amount\":{\"value\":1000,\"currencyCode\":\"GBP\"},\"reference\":\"Order 234\",\"description\":\"Demo payment intent\"}");

CURLcode ret = curl_easy_perform(hnd);

