const request = require('request');

const options = {
  method: 'POST',
  url: 'https://api.dojo.tech/api/payment-intents',
  headers: {'content-type': 'application/json'},
  body: {
    captureMode: 'Auto',
    amount: {value: 0, currencyCode: 'string'},
    reference: 'string',
    description: 'string',
    customer: {emailAddress: 'string', phoneNumber: 'string'},
    billingAddress: {
      address1: 'string',
      address2: 'string',
      address3: 'string',
      address4: 'string',
      city: 'London',
      state: 'string',
      postcode: 'string',
      countryCode: 'GB',
      locality: 'string',
      county: 'string'
    },
    shippingDetails: {
      name: 'string',
      deliveryNotes: 'string',
      address: {
        address1: 'string',
        address2: 'string',
        address3: 'string',
        address4: 'string',
        city: 'London',
        state: 'string',
        postcode: 'string',
        countryCode: 'GB',
        locality: 'string',
        county: 'string'
      }
    },
    transactionSource: 'MOTO',
    config: {
      title: 'string',
      redirectUrl: 'http://example.com',
      cancelUrl: 'http://example.com',
      customerEmail: {collectionRequired: false},
      details: {showTotal: true},
      billingAddress: {collectionRequired: true},
      shippingDetails: {collectionRequired: true},
      payment: {customAmountAllowed: true, tipsAllowed: true}
    },
    requestSecurity: {userAgent: 'string', ipAddress: 'string', device: 'string'},
    itemLines: [
      {
        id: 'string',
        quantity: 0,
        caption: 'string',
        amountTotal: {value: 0, currencyCode: 'string'}
      }
    ],
    taxLines: [
      {
        id: 'string',
        caption: 'string',
        subCaption: 'string',
        amountTotal: {value: 0, currencyCode: 'string'}
      }
    ],
    actionLink: {
      url: 'http://example.com',
      action: 'DownloadFile',
      label: 'string',
      fileName: 'string'
    },
    paymentSource: 'string',
    metadata: {property1: 'string', property2: 'string'}
  },
  json: true
};

request(options, function (error, response, body) {
  if (error) throw new Error(error);

  console.log(body);
});

